import { useHistory, useParams } from "react-router-dom";
import '../../Css/GroupItem.css';
import groupItem_image from '../../img/groupItem_image.jpg';
import groupItem_image14 from '../../img/groupitem_image14.PNG';
import groupItem_image11 from '../../img/groupitem_image11.jpg';
import groupItem_image12 from '../../img/groupitem_image12.jpg';
import groupItem_image13 from '../../img/groupitem_image13.png';
import React, { useState, useEffect } from 'react';
import { NavLink} from 'react-router-dom';
import axios from 'axios';

function GroupItemDetail(props) {

    let { id } = useParams();

    const photos = [groupItem_image11, groupItem_image12, groupItem_image13, groupItem_image14]

    const [groupItem, setGroupItem] = useState("");

    useEffect(()=>{
        axios.get("/groupItem/detail/"+id).then((response)=>{
            if(response.data){
                console.log(response.data);
                setGroupItem(response.data);
            }else{
                alert("failed to");
            }
        });
    }, []);


    return (
        

<ul style={menuStyle}>
<li style={listStyle}>
  <NavLink
    to={`/kimchis/${id}/description`}
    style={({ isActive }) =>
      isActive ? activeLinkStyle : inactiveLinkStyle
    }
  >
    상품설명
  </NavLink>
</li>
<li style={listStyle}>
  <NavLink
    to={`/kimchis/${id}/recipe`}
    style={({ isActive }) =>
      isActive ? activeLinkStyle : inactiveLinkStyle
    }
  >
    레시피
  </NavLink>
</li>
<li style={listStyle}>
  <NavLink
    to={`/kimchis/${id}/review`}
    style={({ isActive }) =>
      isActive ? activeLinkStyle : inactiveLinkStyle
    }
  >
    후기
  </NavLink>
</li>
</ul>
      );


}

const menuStyle = {
    width: '100%',
    padding: '10px',
    boxSizing: 'border-box',
    display: 'flex',
    margin: '30px 0',
  };
  
  const listStyle = {
    border: '1px solid #c2c2c2',
    textAlign: 'center',
    flex: '1 1 auto',
  };
  
  const activeLinkStyle = {
    display: 'block',
    padding: '20px 0',
    textDecoration: 'none',
    color: 'black',
    fontSize: '16px',
  };
  
  const inactiveLinkStyle = {
    backgroundColor: '#eeeeee',
    display: 'block',
    padding: '20px 0',
    textDecoration: 'none',
    color: 'black',
    fontSize: '16px',
  };
export default GroupItemDetail;
