import styles from "../../Css/Cart.module.css";
import groupItem_image1 from '../../img/groupitem_image14.PNG';
import groupItem_image2 from '../../img/groupitem_image11.jpg';
import groupItem_image3 from '../../img/groupitem_image12.jpg';
import groupItem_image4 from '../../img/groupitem_image13.png';
import React, { useState, useEffect } from "react";
import axios from 'axios';

function CartList ()  {
    const [list , SetList] = useState([]);
    
    const photos = [groupItem_image1, groupItem_image2,groupItem_image3, groupItem_image4]

    useEffect(()=> {
        axios.get('/cart/hy').then((res)=>{
          SetList(res.data)
          console.log(res)
        })
        .catch(error => console.log(error))
      },[])

      const length= list.length;

      console.log(length)
    return ( 
        <>
        {length>0 ?(
        <div>
             
        {
            list.map(function(a,i){
                return(
                     (
                    <section className={styles.cart_product_list}>
                  <input type="checkbox" />      
                  <div className={styles.cart_product_wrap}>
                  <div className={styles.cart_product_image}>
                      <img src={groupItem_image1} alt="product-img" />
                    </div>
          
                    <div className={styles.cart_product_info}>
                      <p className={styles.product_name}>{list[i].groupItem_name}</p>
                      <p className={styles.price}>{list[i].groupItem_price}</p>
                      <p className={styles.delivery}>택배배송 / 무료배송</p>
                    </div>
                  </div>
          
                  <div className={styles.cart_product_count}>
                    <img
                      className={styles.minus}
                      src="/icon-minus-line.svg"
                      alt="minus"
                    />
          
                    <div className={styles.count}>
                      <span>5</span>
                    </div>
                    <img
                      className={styles.plus}
                      src="/icon-plus-line.svg"
                      alt="plus"
                    />
                  </div>
                  
                  <div className={styles.cart_product_price}>
                    <p className={styles.total_price}></p>
                    <button className={styles.btn_submit}>주문하기</button>
                  </div>
          
                  <div className={styles.product_remove}>
                    <img src="/icon-delete.svg" alt="delete" />
                    </div>
                   
                    </section>
                )      )     
          })}
           </div> 
             )

             :(<div className={styles.not}>
              <h2>장바구니에 담긴 상품이 없습니다.</h2>
              <p>원하는 상품을 장바구니에 담아보세요!</p>
          </div> )   
            
             }  

    
                   </> 
    );
}

export default CartList;
